import React, { useState } from 'react'

const AddInput = () => {

    const [inputDaata,setInputData] =useState('')
    const [status,setStatus] =useState('empty')
    const [addButtondisable,setaddButtondisable] =useState('')


    const handlInput=(e)=>{
        e.preventDefault()
        setInputData(e.target.value)  
        setStatus("submit")
    }
    const addClick=(e)=>{
        alert(e.target)
    }
    console.log(inputDaata)

  return (
    <div className="inputBox">
        <input type="text" value={inputDaata} onChange={handlInput} placeholder='Enter todo' />
        <button disabled={status !== "submit" || inputDaata.trim() === ''} type='submit' onClick={addClick}>Add</button>
   </div>
  )
}

export default AddInput
