import React, { useEffect, useState } from 'react'

const AddInput = ({onAdd}) => {

    const [inputDaata,setInputData] =useState('')
    const [status,setStatus] =useState('empty')
    const [addButtondisable,setAddButtondisable] =useState(true)


    const handlInput=(e)=>{
        e.preventDefault()
        setInputData(e.target.value)  
        setStatus("submit")
        setAddButtondisable( status === "empty" || inputDaata.trim() === " " ? true :false )
    }
    const addClick=(e)=>{
        alert("hellow")

        try {
            setInputData('')
            addButtondisable(true)
            onAdd({
            title:inputDaata,
            complete:false
           })
            
        } catch (error) {
            console.log(error)}
    }

    // useEffect(()=>{
    //     handlInput
    // },[inputDaata])

    // console.log( "state      "+inputDaata)
    // console.log( "button      "+addButtondisable)
 

  return (

    <div className="inputBox">
        <input type="text" value={inputDaata} onChange={handlInput} placeholder='Enter todo' />
        <button disabled={addButtondisable} style={{backgroundColor:addButtondisable? "#666":"white"}} type='submit' onClick={addClick}>Add</button>
   </div>
  )
}

export default AddInput
