import React, { useState } from 'react'
import './todo.css'
import AddInput from './AddInput'
// import * as images from '../assets/img'
// import plus from '../assets/img/plus.png'
import trash from '../assets/img/trash.png'
import check from '../assets/img/checked.png'

function Todo() {

    const [todos,setTodos]=useState([])

    const addItem=(toodoo)=>{
        con
        setTodos((p)=>{
            return {
                todos:[...p.todos,toodoo]}
        })

        alert("hellow")
    }
    // setTodos({todos:toodoo})
    console.log(todos)

  return (
    <div className='todoBox'>
        <AddInput onAdd={addItem}/>
        <div className="listBox ">
            <h1 className="titleListBox">my Todo</h1>
            <div className="listsbox">
                <ul className="lists">
                  {/* 1 */}
                   <li className="list">
                    <div className="list_box">
                        <span>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deleniti, atque!</span>
                            <div className="buts">
                                <button><img src={check} alt="" /></button>
                                {/* <button><img src={plus} alt="" /></button> */}
                                <button><img src={trash} alt="" /></button>
                            </div>
                        </div>  
                   </li>
                  {/* 2 */}
                   <li className="list">
                    <div className="list_box">
                        <span>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deleniti, atque!</span>
                        <div className="buts">
                                <button><img src={check} alt="" /></button>
                                {/* <button><img src={plus} alt="" /></button> */}
                                <button><img src={trash} alt="" /></button>
                            </div>
                        </div>  
                   </li>
                  
                </ul>
            </div>
        </div>
    </div>
  )
}

export default Todo
